#include "libft.h"

char	*ft_strchr(const char *s, int i)
{
	const char	c = i;

	while (1)
	{
		while (*s)
		{
			if (*s == c)
				break ;
			s++;
		}
		if (*s == c)
			break ;
		return (NULL);
	}
	return ((char *)s);
}
